function Login_Screen_js() {
    /* Object & array with components "name-to-id" mapping */
    var n2id_buf = {
        'mobiletextinput_9': 'Login_Screen_mobiletextinput_9',
        'mobiletextinput_10': 'Login_Screen_mobiletextinput_10',
        'loginbutton': 'Login_Screen_loginbutton',
        'registerbutton': 'Login_Screen_registerbutton'
    };
    if ("n2id" in window && window.n2id !== undefined) {
        $.extend(n2id, n2id_buf);
    } else {
        window.n2id = n2id_buf;
    }
    /*
     * Nonvisual components
     */
    Apperyio.mappings = Apperyio.mappings || {};
    Apperyio.mappings["Login_Screen_Login_Screen_onpageshow_mapping_0"] = {
        "homeScreen": "Login_Screen",
        "directions": []
    };
    Apperyio.mappings["Login_Screen_loginService_onsuccess_mapping_0"] = {
        "homeScreen": "Login_Screen",
        "directions": []
    };
    Apperyio.mappings["Login_Screen_loginService_onbeforesend_mapping_0"] = {
        "homeScreen": "Login_Screen",
        "directions": [
            {
                "from_name": "Login_Screen",
                "from_type": "UI",
                "to_name": "loginService",
                "to_type": "SERVICE_REQUEST",
                "to_default": {
                    "headers": {
                        "X-Appery-Database-Id": "{database_id}",
                        "Content-Type": "application/json"
                    },
                    "parameters": {},
                    "body": null
                },
                "mappings": [
                    {
                        "source": "$['mobiletextinput_9:text']",
                        "target": "$['body']['username']"
                    },
                    {
                        "source": "$['mobiletextinput_10:text']",
                        "target": "$['body']['password']"
                    }
                ]
            }
        ]
    };
    Apperyio.datasources = Apperyio.datasources || {};
    window.loginService = Apperyio.datasources.loginService = new Apperyio.DataSource(UserDB_login_service, {
        "onBeforeSend": function(jqXHR) {
            Apperyio.processMappingAction(Apperyio.mappings["Login_Screen_loginService_onbeforesend_mapping_0"]);
        },
        "onComplete": function(jqXHR, textStatus) {
        },
        "onSuccess": function(data) {
            Apperyio.processMappingAction(Apperyio.mappings["Login_Screen_loginService_onsuccess_mapping_0"]);
            Apperyio.navigateTo('startScreen', {
                reverse: false
            });
        },
        "onError": function(jqXHR, textStatus, errorThrown) {
            Apperyio.navigateTo('sorry_page', {
                reverse: false
            });
        }
    });
    Apperyio.CurrentScreen = 'Login_Screen';
    _.chain(Apperyio.mappings)
        .filter(function(m) {
            return m.homeScreen === Apperyio.CurrentScreen;
        })
        .each(Apperyio.UIHandler.hideTemplateComponents);
    /*
     * Events and handlers
     */
    // On Load
    var Login_Screen_onLoad = function() {
        Login_Screen_elementsExtraJS();
        Login_Screen_deviceEvents();
        Login_Screen_windowEvents();
        Login_Screen_elementsEvents();
    };
    // screen window events
    function Login_Screen_windowEvents() {
        $('#Login_Screen').bind('pageshow orientationchange', function() {
            var _page = this;
            adjustContentHeightWithPadding(_page);
        });
        $('#Login_Screen').on({
            pageshow: function(event) {
                Apperyio.processMappingAction(Apperyio.mappings["Login_Screen_Login_Screen_onpageshow_mapping_0"]);
                Apperyio("userInfoList").show();
            },
        });
    };
    // device events
    function Login_Screen_deviceEvents() {
        document.addEventListener("deviceready", function() {
        });
    };
    // screen elements extra js
    function Login_Screen_elementsExtraJS() {
        // screen (Login_Screen) extra code
    };
    // screen elements handler
    function Login_Screen_elementsEvents() {
        $(document).on("click", "a :input,a a,a fieldset label", function(event) {
            event.stopPropagation();
        });
        $(document).off("click", '#Login_Screen_mobilecontainer [name="loginbutton"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    try {
                        loginService.execute({});
                    } catch (e) {
                        console.error(e);
                        hideSpinner();
                    };
                }
            },
        }, '#Login_Screen_mobilecontainer [name="loginbutton"]');
        $(document).off("click", '#Login_Screen_mobilecontainer [name="registerbutton"]').on({
            click: function(event) {
                if (!$(this).attr('disabled')) {
                    Apperyio.navigateTo('Register', {
                        reverse: false
                    });
                }
            },
        }, '#Login_Screen_mobilecontainer [name="registerbutton"]');
    };
    $(document).off("pagebeforeshow", "#Login_Screen").on("pagebeforeshow", "#Login_Screen", function(event, ui) {
        Apperyio.CurrentScreen = "Login_Screen";
        _.chain(Apperyio.mappings)
            .filter(function(m) {
                return m.homeScreen === Apperyio.CurrentScreen;
            })
            .each(Apperyio.UIHandler.hideTemplateComponents);
    });
    Login_Screen_onLoad();
};
$(document).off("pagecreate", "#Login_Screen").on("pagecreate", "#Login_Screen", function(event, ui) {
    Apperyio.processSelectMenu($(this));
    Login_Screen_js();
});